mapper.select:
select count(*)
from employees



			department_id = #{keyword}


			 job_id like '%' || #{keyword} ||'%'


			first_name like '%' || #{keyword} ||'%'


			salary >= #{keyword}

select id, name, dept, job, phone, salary, comm, hiredate from (select row_number() over(order by ${sort} ${orderby}) rn, employee_id as id, first_name as name, department_id as dept, job_id as job, phone_number as phone, nvl(salary, 0) salary, nvl(commission_pct, 0) comm, hire_date as hiredate from employees
department_id = #{keyword}
job_id like '%' || #{keyword} ||'%'
first_name like '%' || #{keyword} ||'%'
salary >= #{keyword}
) where rn >= #{beginIndex} and rn <= #{endIndex}
