mapper.select:
select 
	count(*)
from 
	B_BOARD



			B_TITLE like '%' || #{keyword} || '%'


			B_WRITER like '%' || #{keyword} || '%'


			B_CONTENTS like '%' || #{keyword} || '%'



		and B_CATEGORY = #{searchBoardType}


		and B_DEPARTMENT = #{department}


		and B_SUBJECTNO = #{subjectNo}

mapper.update:
update B_BOARD
b_title = #{title},
b_contents = #{contents},
B_FILE = #{fileName}
where b_no = #{bno}

mapper.select:
select no, title, contents, writer, regdate, countView, category, fileName, department, subjectNo from ( select row_number() over(order by b_no desc) rn, B_NO as no, B_TITLE as title, B_CONTENTS as contents, B_WRITER as writer, B_REGDATE as regdate, B_COUNT as countView, B_CATEGORY as category, B_FILE as fileName, B_DEPARTMENT as department, B_SUBJECTNO as subjectNo from b_board
B_TITLE like '%' || #{keyword} || '%'
B_WRITER like '%' || #{keyword} || '%'
B_CONTENTS like '%' || #{keyword} || '%'
and B_CATEGORY = #{searchBoardType}
and b_department = #{department}
and b_subjectno = #{subjectNo}
) where rn >= #{beginIndex} and rn <= #{endIndex}
