mapper.update:
update B_BOARD
b_title = #{title},
b_contents = #{contents},
B_FILE = #{fileName}
where b_no = #{bno}

mapper.select:
select no, title, contents, writer, regdate, countView, category, fileName, department from ( select row_number() over(order by b_no desc) rn, b_no as no, b_title as title, b_contents as contents, b_writer as writer, b_regdate as regdate, b_count as countView, b_category as category, b_file as fileName, b_department as department from b_board
B_TITLE like '%' || #{keyword} || '%'
B_WRITER like '%' || #{keyword} || '%'
B_CONTENTS like '%' || #{keyword} || '%'
) where rn >= #{beginIndex} and rn <= #{endIndex}
