mapper.select:
select U_STU_NO as no, U_STU_ADDR1 as addr1, U_STU_ADDR2 as addr2, U_STU_DIVISION, t_name as division, U_STU_EMAIL as email, U_STU_ENTERDATE as enterDate, U_STU_GENDER as gender, U_STU_GRADE as grade, U_STU_ID as id, U_STU_LEAVEDATE as leaveDate, U_STU_NAME as name, U_STU_NO as no, U_STU_PARENTNAME as parentName, U_STU_PARENTPHONE as parentPhone, U_STU_PHONE as phone, U_STU_PROFESSOR as professor, U_STU_PWD as pwd, c_name as register, U_STU_SCORE as score, U_STU_SSN as ssn from U_STUDENT a, t_sitemap b,C_CODEREGISTER C where a.u_stu_division = b.t_code and a.u_stu_register = c.c_code
and C.c_code LIKE '%' || #{register} || '%'
and ${sort} = #{q}
and U_STU_GRADE = #{grade}
and U_STU_DIVISION = #{division}

select u_prof_no as no, u_prof_id as id, u_prof_name as name, u_prof_gender as gender, u_prof_code as code, T_NAME as division, u_prof_pwd as pwd, C_NAME as register, u_prof_phone as phone, u_prof_addr as addr, u_prof_email as email, u_prof_grade as grade, u_prof_ssn as ssn, u_prof_forei as forei, u_prof_entrydate as entrydate, u_prof_leavedate as leavedate from u_professor A, C_CODEREGISTER B, T_SITEMAP C where A.U_PROF_REGISTER = B.C_CODE AND A.U_PROF_DIVISION = C.T_CODE
and U_PROF_REGISTER = #{register}
and ${sort} = #{q}
and u_prof_grade = #{grade}
and U_prof_DIVISION = #{division}

select U_STU_NO as no, U_STU_ADDR1 as addr1, U_STU_ADDR2 as addr2, t_name as division, U_STU_EMAIL as email, U_STU_ENTERDATE as enterDate, U_STU_GENDER as gender, U_STU_GRADE as grade, U_STU_ID as id, U_STU_LEAVEDATE as leaveDate, U_STU_NAME as name, U_STU_NO as no, U_STU_PARENTNAME as parentName, U_STU_PARENTPHONE as parentPhone, U_STU_PHONE as phone, U_STU_PROFESSOR as professor, U_STU_PWD as pwd, c_name as register, U_STU_SCORE as score, U_STU_SSN as ssn
from U_STUDENT a, t_sitemap b,C_CODEREGISTER C where a.u_stu_division = b.t_code and a.u_stu_register = c.c_code
and u_stu_register = #{register}
and u_stu_register in ('AD1000', 'AD2000')
and ${sort} = #{q}
and SUBSTR(u_stu_id, 1, 2) = #{grade}
and U_STU_DIVISION = #{division}

SELECT L_NO as no, b.u_STU_NAME as "student.name", b.u_STU_id as "student.id", c.t_name as "student.division", d.c_name as code, L_ENROLLDATE as enrollDate, L_PASS as pass, L_OKAYDATE as okDate, L_REINDATE as reinDate, L_SCOREPASS as scorePass FROM L_LEAVE a, u_student b, t_sitemap c, c_coderegister d WHERE a.STU_NO = b.u_stu_no and b.u_stu_division = c.t_code and a.c_code = d.c_code and L_PASS = 'false'
and d.c_code = #{register}
and ${sort} = #{q}
and b.U_STU_DIVISION = #{division}

SELECT L_NO as no, b.u_STU_NAME as "student.name", b.u_STU_id as "student.id", c.t_name as "student.division", d.c_name as code, L_ENROLLDATE as enrollDate, L_PASS as pass, L_OKAYDATE as okDate, L_REINDATE as reinDate, L_SCOREPASS as scorePass FROM L_LEAVE a, u_student b, t_sitemap c, c_coderegister d WHERE a.STU_NO = b.u_stu_no and b.u_stu_division = c.t_code and a.c_code = d.c_code and L_PASS != 'false'
and d.c_code = #{register}
and ${sort} = #{q}
and b.U_STU_DIVISION = #{division}

mapper.update:
update
	u_student
set
	u_stu_register = #{code}
where
	u_stu_id = #{student.id}

mapper.select:
SELECT R_NO as "no", d.C_NAME as code, a.STU_no as "student.no", R_REINDATE as reinDate, R_PASS as pass, R_PASSDATE as passDate, R_ENROLLDATE as enrollDate, R_LEAVENO as "leave.no" , c.T_NAME as "student.division", b.U_STU_ID as "student.id", b.U_STU_name as "student.name" FROM R_REINSTATEMENT a, U_STUDENT b, T_SITEMAP c,C_CODEREGISTER d WHERE a.c_code = d.C_CODE and a.STU_NO = b.U_STU_NO and b.U_STU_DIVISION = c.T_CODE and a.r_pass='false'
and ${sort} = #{q}
and b.U_STU_DIVISION = #{division}

SELECT R_NO as "no", d.C_NAME as code, a.STU_no as "student.no", R_REINDATE as reinDate, R_PASS as pass, R_PASSDATE as passDate, R_ENROLLDATE as enrollDate, R_LEAVENO as "leave.no" , c.T_NAME as "student.division", b.U_STU_ID as "student.id", b.U_STU_name as "student.name" FROM R_REINSTATEMENT a, U_STUDENT b, T_SITEMAP c,C_CODEREGISTER d WHERE a.c_code = d.C_CODE and a.STU_NO = b.U_STU_NO and b.U_STU_DIVISION = c.T_CODE and a.r_pass='true'
and ${sort} = #{q}
and b.U_STU_DIVISION = #{division}

SELECT D_NO as no, d.c_name as code, D_STUNO as "student.no", D_PASS as pass, D_PASSDATE as passDate, D_ENROLLDATE as enrollDate, c.T_NAME as "student.division", b.U_STU_ID as "student.id", b.U_STU_name as "student.name" FROM D_DROPOFF a, U_STUDENT b, T_SITEMAP c,C_CODEREGISTER d WHERE a.D_code = d.C_CODE and a.D_STUNO = b.U_STU_NO and b.U_STU_DIVISION = c.T_CODE and D_PASS = 'false'
and ${sort} = #{q}
and b.U_STU_DIVISION = #{division}

SELECT D_NO as no, d.c_name as code, D_STUNO as "student.no", D_PASS as pass, D_PASSDATE as passDate, D_ENROLLDATE as enrollDate, c.T_NAME as "student.division", b.U_STU_ID as "student.id", b.U_STU_name as "student.name" FROM D_DROPOFF a, U_STUDENT b, T_SITEMAP c,C_CODEREGISTER d WHERE a.D_code = d.C_CODE and a.D_STUNO = b.U_STU_NO and b.U_STU_DIVISION = c.T_CODE and D_PASS != 'false'
and ${sort} = #{q}
and b.U_STU_DIVISION = #{division}
